import React, { useState } from "react";

function App() {
  const [country, setCountry] = useState("");
  const [city, setCity] = useState("");
  const [days, setDays] = useState("");
  const [budget, setBudget] = useState("");
  const [plan, setPlan] = useState(null);

  const generatePlan = () => {
    setPlan({
      country,
      city,
      days,
      budget,
      recommendation: `Explore the beautiful city of ${city}, ${country} for ${days} days within a budget of $${budget}.`,
    });
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>AI Travel Planner</h1>
      <input
        type="text"
        placeholder="Enter country"
        value={country}
        onChange={(e) => setCountry(e.target.value)}
      />
      <br />
      <input
        type="text"
        placeholder="Enter city"
        value={city}
        onChange={(e) => setCity(e.target.value)}
      />
      <br />
      <input
        type="number"
        placeholder="Number of days"
        value={days}
        onChange={(e) => setDays(e.target.value)}
      />
      <br />
      <input
        type="number"
        placeholder="Enter budget"
        value={budget}
        onChange={(e) => setBudget(e.target.value)}
      />
      <br />
      <button onClick={generatePlan} style={{ marginTop: "10px", padding: "5px 15px" }}>
        Generate Plan
      </button>

      {plan && (
        <div style={{ marginTop: "20px", padding: "10px", border: "1px solid black" }}>
          <h2>Your Travel Plan</h2>
          <p><strong>Country:</strong> {plan.country}</p>
          <p><strong>City:</strong> {plan.city}</p>
          <p><strong>Days:</strong> {plan.days}</p>
          <p><strong>Budget:</strong> ${plan.budget}</p>
          <p>{plan.recommendation}</p>
        </div>
      )}
    </div>
  );
}

export default App;
