import React, { useState } from "react";

function App() {
  const [destination, setDestination] = useState("");
  const [city, setCity] = useState("");
  const [itinerary, setItinerary] = useState(null);
  const [weather, setWeather] = useState(null);

  const fetchItinerary = async () => {
    if (!destination) return alert("Please enter a destination.");
    try {
      const response = await fetch(`http://127.0.0.1:8000/api/itinerary?destination=${destination}`);
      const data = await response.json();
      setItinerary(data);
    } catch (error) {
      console.error("Error fetching itinerary:", error);
    }
  };

  const fetchWeather = async () => {
    if (!city) return alert("Please enter a city.");
    try {
      const response = await fetch(`http://127.0.0.1:8000/api/weather?city=${city}`);
      const data = await response.json();
      setWeather(data);
    } catch (error) {
      console.error("Error fetching weather:", error);
    }
  };

  return (
    <div style={{ textAlign: "center", fontFamily: "Arial, sans-serif", padding: "20px" }}>
      <h1>üåç AI Travel Planner</h1>

      {/* Itinerary Input */}
      <div>
        <input
          type="text"
          placeholder="Enter destination (e.g., Paris)"
          value={destination}
          onChange={(e) => setDestination(e.target.value)}
          style={{ padding: "10px", margin: "5px", borderRadius: "5px" }}
        />
        <button onClick={fetchItinerary} style={{ padding: "10px", borderRadius: "5px", background: "blue", color: "white" }}>
          Get Itinerary
        </button>
      </div>

      {/* Weather Input */}
      <div>
        <input
          type="text"
          placeholder="Enter city (e.g., New York)"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          style={{ padding: "10px", margin: "5px", borderRadius: "5px" }}
        />
        <button onClick={fetchWeather} style={{ padding: "10px", borderRadius: "5px", background: "green", color: "white" }}>
          Get Weather
        </button>
      </div>

      {/* Display Itinerary */}
      {itinerary && (
        <div style={{ marginTop: "20px", padding: "15px", border: "1px solid black", borderRadius: "10px" }}>
          <h2>üìå Itinerary for {destination}</h2>
          <p>{itinerary.itinerary}</p>
        </div>
      )}

      {/* Display Weather */}
      {weather && (
        <div style={{ marginTop: "20px", padding: "15px", border: "1px solid black", borderRadius: "10px" }}>
          <h2>üå§ Weather in {city}</h2>
          <p>Temperature: {weather.weather.temperature}¬∞C</p>
          <p>Condition: {weather.weather.description}</p>
        </div>
      )}
    </div>
  );
}

export default App;
