import React, { useState } from "react";

function TravelPlanner() {
  const [destination, setDestination] = useState("");
  const [days, setDays] = useState(1);
  const [interests, setInterests] = useState("");
  const [itinerary, setItinerary] = useState("");

  const handleGenerate = async () => {
    const response = await fetch("http://127.0.0.1:8000/generate-itinerary/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ destination, days, interests }),
    });
    const data = await response.json();
    setItinerary(data.itinerary);
  };

  return (
    <div>
      <h2>AI Travel Planner</h2>
      <input type="text" placeholder="Destination" onChange={(e) => setDestination(e.target.value)} />
      <input type="number" min="1" placeholder="Days" onChange={(e) => setDays(e.target.value)} />
      <input type="text" placeholder="Interests" onChange={(e) => setInterests(e.target.value)} />
      <button onClick={handleGenerate}>Generate Itinerary</button>
      <p>{itinerary}</p>
    </div>
  );
}

export default TravelPlanner;
