import React, { useState } from "react";

function ItineraryComponent() {
  const [destination, setDestination] = useState("");
  const [days, setDays] = useState(3);
  const [interests, setInterests] = useState("");
  const [itinerary, setItinerary] = useState("");

  const fetchItinerary = async () => {
    try {
      const response = await fetch(
        `http://127.0.0.1:8000/api/itinerary?destination=${destination}&days=${days}&interests=${interests}`
      );
      const data = await response.json();
      setItinerary(data.itinerary);
    } catch (error) {
      console.error("Error fetching itinerary:", error);
    }
  };

  return (
    <div>
      <h2>ðŸ›« AI Travel Itinerary Planner</h2>
      <input type="text" placeholder="Enter Destination" value={destination} onChange={(e) => setDestination(e.target.value)} />
      <input type="number" placeholder="Days" value={days} onChange={(e) => setDays(e.target.value)} />
      <input type="text" placeholder="Interests (e.g., culture, food)" value={interests} onChange={(e) => setInterests(e.target.value)} />
      <button onClick={fetchItinerary}>Generate Itinerary</button>
      <pre>{itinerary}</pre>
    </div>
  );
}

function App() {
  return (
    <div>
      <h1>ðŸš€ AI Travel Planner</h1>
      <ItineraryComponent />
    </div>
  );
}

export default App;
