import React, { useState } from "react";

function ItineraryComponent() {
  const [destination, setDestination] = useState("");
  const [days, setDays] = useState(3);
  const [interests, setInterests] = useState("");
  const [itinerary, setItinerary] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchItinerary = async () => {
    if (!destination.trim() || days <= 0 || !interests.trim()) {
      setError("Please fill in all fields correctly.");
      return;
    }
    
    setError("");
    setLoading(true);
    
    try {
      const response = await fetch(
        `http://127.0.0.1:8000/api/itinerary?destination=${encodeURIComponent(destination)}&days=${days}&interests=${encodeURIComponent(interests)}`
      );
      const data = await response.json();

      if (data.error) {
        setError(data.error);
        setItinerary("");
      } else {
        setItinerary(data.itinerary);
      }
    } catch (error) {
      setError("Failed to fetch itinerary. Please try again.");
      setItinerary("");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "500px", margin: "auto", textAlign: "center" }}>
      <h2>ðŸ›« AI Travel Itinerary Planner</h2>
      <input
        type="text"
        placeholder="Enter Destination"
        value={destination}
        onChange={(e) => setDestination(e.target.value)}
        style={{ display: "block", width: "100%", marginBottom: "10px", padding: "8px" }}
      />
      <input
        type="number"
        placeholder="Days"
        value={days}
        onChange={(e) => setDays(Number(e.target.value))}
        style={{ display: "block", width: "100%", marginBottom: "10px", padding: "8px" }}
        min="1"
      />
      <input
        type="text"
        placeholder="Interests (e.g., culture, food)"
        value={interests}
        onChange={(e) => setInterests(e.target.value)}
        style={{ display: "block", width: "100%", marginBottom: "10px", padding: "8px" }}
      />
      <button
        onClick={fetchItinerary}
        disabled={loading}
        style={{
          padding: "10px",
          width: "100%",
          backgroundColor: "#007bff",
          color: "white",
          border: "none",
          cursor: "pointer",
        }}
      >
        {loading ? "Generating..." : "Generate Itinerary"}
      </button>

      {error && <p style={{ color: "red", marginTop: "10px" }}>{error}</p>}
      
      {itinerary && (
        <pre
          style={{
            whiteSpace: "pre-wrap",
            wordWrap: "break-word",
            textAlign: "left",
            backgroundColor: "#f4f4f4",
            padding: "10px",
            marginTop: "10px",
            borderRadius: "5px",
          }}
        >
          {itinerary}
        </pre>
      )}
    </div>
  );
}

function App() {
  return (
    <div>
      <h1 style={{ textAlign: "center" }}>ðŸš€ AI Travel Planner</h1>
      <ItineraryComponent />
    </div>
  );
}

export default App;
