import React from "react";
import { useState } from "react";
import axios from "../services/api";

const TripPlanner = () => {
  const [destination, setDestination] = useState("");
  const [days, setDays] = useState(1);
  const [itinerary, setItinerary] = useState(null);

  const generatePlan = async () => {
    try {
      const response = await axios.post("/generate-itinerary", { destination, days });
      setItinerary(response.data);
    } catch (error) {
      console.error("Error generating itinerary:", error);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">AI Trip Planner</h1>
      <div className="mb-4">
        <label className="block font-semibold">Destination:</label>
        <input
          type="text"
          value={destination}
          onChange={(e) => setDestination(e.target.value)}
          className="border p-2 w-full"
        />
      </div>
      <div className="mb-4">
        <label className="block font-semibold">Number of Days:</label>
        <input
          type="number"
          value={days}
          onChange={(e) => setDays(e.target.value)}
          className="border p-2 w-full"
          min="1"
        />
      </div>
      <button onClick={generatePlan} className="bg-blue-600 text-white px-4 py-2 rounded">
        Generate Itinerary
      </button>

      {itinerary && (
        <div className="mt-6 bg-gray-100 p-4 rounded">
          <h2 className="text-lg font-bold">Your AI-Powered Itinerary</h2>
          <pre className="mt-2">{JSON.stringify(itinerary, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default TripPlanner;
