// index.js
const express = require('express');
const cors = require('cors');
const bodyParser = require('body-parser');
const authenticationController = require('./controllers/authenticationController'); // Corrected file name

const app = express();

app.use(cors());
app.use(bodyParser.json());

// Use authenticationController for authentication routes
app.use('/api/auth', authenticationController);

const PORT = process.env.PORT || 5000;
app.listen(PORT, () => {
  console.log(`Server running on port ${PORT}`);
});
import React, { Suspense, lazy } from "react";
import ReactDOM from "react-dom/client";
import { BrowserRouter } from "react-router-dom";
import "./index.css"; // Ensure this file exists
import ErrorBoundary from "./components/ErrorBoundary"; // Custom error handler

// Lazy load the App component for better performance
const App = lazy(() => import("./App"));

// Create Root
const root = ReactDOM.createRoot(document.getElementById("root"));

root.render(
  <React.StrictMode>
    <BrowserRouter>
      <ErrorBoundary>
        {/* Suspense for lazy loading */}
        <Suspense fallback={<div className="loading">Loading...</div>}>
          <App />
        </Suspense>
      </ErrorBoundary>
    </BrowserRouter>
  </React.StrictMode>
);
