import React, { useEffect, useState } from "react";

function App() {
  const [itinerary, setItinerary] = useState("Loading itinerary...");
  const [weather, setWeather] = useState("Loading weather...");

  useEffect(() => {
    // Fetch Itinerary
    fetch("http://127.0.0.1:8000/api/itinerary?destination=Paris")
      .then((response) => response.json())
      .then((data) => setItinerary(JSON.stringify(data)))
      .catch((error) => console.error("Error fetching itinerary:", error));

    // Fetch Weather
    fetch("http://127.0.0.1:8000/api/weather?city=New York")
      .then((response) => response.json())
      .then((data) => setWeather(JSON.stringify(data)))
      .catch((error) => console.error("Error fetching weather:", error));
  }, []);

  return (
    <div>
      <h1>AI Travel Planner</h1>
      <h2>Itinerary: {itinerary}</h2>
      <h2>Weather: {weather}</h2>
    </div>
  );
}

export default App;
