import React, { useState } from "react";
import { fetchItinerary } from "../services/api";

const ItineraryComponent = () => {
    const [destination, setDestination] = useState("");
    const [days, setDays] = useState(3);
    const [interests, setInterests] = useState("Culture");
    const [itinerary, setItinerary] = useState("");

    const generateItinerary = async () => {
        const result = await fetchItinerary(destination, days, interests);
        setItinerary(result);
    };

    return (
        <div style={{ textAlign: "center", padding: "20px" }}>
            <h2>üåç AI Travel Itinerary</h2>
            <input
                type="text"
                placeholder="Enter Destination"
                value={destination}
                onChange={(e) => setDestination(e.target.value)}
            />
            <select value={days} onChange={(e) => setDays(Number(e.target.value))}>
                {[3, 5, 7].map((day) => (
                    <option key={day} value={day}>{day} Days</option>
                ))}
            </select>
            <select value={interests} onChange={(e) => setInterests(e.target.value)}>
                {["Culture", "Adventure", "Food", "Nature"].map((interest) => (
                    <option key={interest} value={interest}>{interest}</option>
                ))}
            </select>
            <button onClick={generateItinerary}>Generate</button>

            {itinerary && (
                <div style={{ marginTop: "20px", border: "1px solid gray", padding: "15px" }}>
                    <h3>üìç {destination} - {days} Days</h3>
                    <p>{itinerary}</p>
                </div>
            )}
        </div>
    );
};

export default ItineraryComponent;
